import React, { useEffect, useState } from "react";
import "./Dashboard.css";

const Dashboard = ({ user }) => {
  const [habits, setHabits] = useState([]);
  const [habitInput, setHabitInput] = useState("");

  const token = localStorage.getItem("token");

  // ✅ Fetch habits
  const fetchHabits = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/habits", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok) setHabits(data);
      else console.error(data.msg);
    } catch (err) {
      console.error("❌ Fetch habits error:", err);
    }
  };

  useEffect(() => {
    fetchHabits();
  }, []);

  // ✅ Add new habit
  const addHabit = async () => {
    if (!habitInput.trim()) return;
    try {
      const res = await fetch("http://localhost:5000/api/habits", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ name: habitInput }),
      });
      const data = await res.json();
      if (res.ok) {
        setHabits([data.habit, ...habits]);
        setHabitInput("");
      } else {
        alert(data.msg);
      }
    } catch (err) {
      console.error("❌ Add habit error:", err);
    }
  };

  // ✅ Delete habit
  const deleteHabit = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/habits/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) setHabits(habits.filter((h) => h._id !== id));
    } catch (err) {
      console.error("❌ Delete habit error:", err);
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Your Habits</h2>
      <div className="input-area">
        <input
          type="text"
          placeholder="Enter a new habit"
          value={habitInput}
          onChange={(e) => setHabitInput(e.target.value)}
        />
        <button onClick={addHabit}>Add Habit</button>
      </div>

      <ul>
        {habits.map((habit) => (
          <li key={habit._id}>
            {habit.name} <button onClick={() => deleteHabit(habit._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
